import '@material/react-button/index.scss';
import { Button } from '@material/react-button';
import '@material/react-text-field/index.scss';
import TextField from '@material/react-text-field';
import { useState } from 'react';
import { Grid } from '@material/react-layout-grid';
import './StudentRecordForm.scss';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function StudentRecordForm(props){
    const [values, setValues] = useState(initializeFValues);
    
    const handleInputChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]:value
        });
    }

        return(
            <form>
                <label>
                    Full Name:
                    <input type="text" 
                            name="fullName" 
                            value={values.fullName} 
                            onChange={handleInputChange} />
                </label>
                <label>
                    Rollno:
                    <input type="text" 
                            name="rollNo" 
                            value={values.rollNo} />
                </label>
                <label>
                    Student Class:
                    <input type="text" 
                            name="studentClass" 
                            value={values.studentClass} />
                </label>
                <label>
                    Total Subjects:
                    <input type="text" 
                            name="totalSubject" 
                            value={values.totalSubjects} />
                </label>
                <label>
                    Age:
                    <input type="text" 
                            name="age" 
                            value={values.age} />
                </label>
                <Button>Submit</Button>
            </form>
        )
}

export default StudentRecordForm;
