import React, {useState} from "react";
import {makeStyles} from '@material-ui/core';

export function useForm(initializeFValues, validateOnChange=false, validate){
    const [values, setValues] = useState(initializeFValues);
    const [errors, setErrors] = useState({});

    const handleInputChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]:value
        })
        if(validateOnChange)
            validate({ [name]: value })
    }

    const restForm = () => {
        setValues(initializeFValues);
        setErrors({})
    }

    return{
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        restForm
    }
}

const useStyles = makeStyles(theme => ({
    root: {
        '& .MuiFormControl-root': {
            width:'100%',
            margin: theme.spacing(1)
        }
    }
}))

export function Form(props){
    const classes = useStyles();
    const  {children, ...other} = props;
    return(
        <form className={classes.root} autoComplete="off" {...other}>
            {props.children}
        </form>
    )
}
