const KEYS = {
    studentRecords: 'studentRecords',
    studentRecordId: 'studentRecordId'
}

export function insertStudentRecords(data){
    let studentRecords = getAllStudentRecords();

    data['id']= generateStudentRecordId();
    studentRecords.push(data);
    
    localStorage.setItem(KEYS.studentRecords, JSON.stringify(data));
}

export function generateStudentRecordId(){
    if(localStorage.getItem(KEYS.studentRecordId) == null)
        localStorage.setItem(KEYS.studentRecordId, '0')
    
        var id = parseInt(localStorage.getItem(KEYS.studentRecordId))
        localStorage.setItem(KEYS.studentRecordId, (++id).toString())

        return id;
}

export function getAllStudentRecords(){
    if(localStorage.getItem(KEYS.studentRecords) == null)
        localStorage.setItem(KEYS.studentRecords, JSON.stringify([]))
    
    return JSON.parse(localStorage.getItem(KEYS.studentRecords));
}
