import React, {useState} from "react";
import {makeStyles} from '@material-ui/core';

export function useForm(initializeFValues){
    const [values, setValues] = useState(initializeFValues);

    const handleInputChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]:value
        });
    }

    const restForm = () => {
        setValues(initializeFValues);
    }

    return{
        values,
        setValues,
        handleInputChange,
        restForm
    }
}

export function Form(props){
    return(
        <form autoComplete="off">
            {props.children}
        </form>
    )
}
