import './StudentRecordForm.scss';
import {useForm, Form} from '../useForm';
import Input from '../controls/Input';
import Button from '../controls/Button';
import {insertStudentRecords} from '../../services/studentRecordService';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

const studentRecords = [
    {fullName: "Rebeca", rollNo: 1, studentClass: 2, totalSubjects: 5, age: 6 },
    {fullName: "Shawn", rollNo: 2, studentClass: 4, totalSubjects: 7, age: 8 },
    {fullName: "Patrik", rollNo: 3, studentClass: 2, totalSubjects: 5, age: 5 },
    {fullName: "Pokoyo", rollNo: 4, studentClass: 3, totalSubjects: 6, age: 7 },
    {fullName: "Elly", rollNo: 5, studentClass: 3, totalSubjects: 6, age: 6 },
    {fullName: "Spongebob", rollNo: 6, studentClass: 2, totalSubjects: 5, age: 5 }
]

function StudentRecordForm(props){
    const {
        values,
        setValues,
        handleInputChange,
        restForm
    } = useForm(initializeFValues);
    
    const handleSubmit = e => {
        e.preventDefault()

        insertStudentRecords(values);
        restForm();
    }

    return(
        <Form>
            <Input
                name= "fullName"
                label= "Full Name:"
                value= {values.fullName}
                onChange= {handleInputChange} />

            <Input
                name= "rollNo"
                label= "Rollno:"
                value= {values.rollNo}
                onChange= {handleInputChange} />
                
            <Input
                name= "studentClass"
                label= "Student Class:"
                value= {values.studentClass}
                onChange= {handleInputChange} />

            <Input
                name= "totalSubjects"
                label= "Total Subjects:"
                value= {values.totalSubjects}
                onChange= {handleInputChange} />

            <Input
                name= "age"
                label= "Age:"
                value= {values.age}
                onChange= {handleInputChange} />
            
            <div>
                <Button 
                    text = "Submit" 
                    type="submit" 
                    onClick= {handleSubmit} />
            </div>
        </Form>
    )
}

export default StudentRecordForm;
