import { TextField } from "@material-ui/core";

export default function(props){
    const {name, label, value, error=null, onChange} = props;

    return(
        <TextField
            variant="outlined"
            label={label}
            name={name}
            value={value}
            onChange={onChange}
            {...(error & {error:true, helperText:error})}
        >
            
        </TextField>
       
    )
}
