import './StudentRecordForm.scss';
import {useForm, Form} from '../useForm';
import Input from '../controls/Input';
import Button from '../controls/Button';
import {insertStudentRecords} from '../../services/studentRecordService';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

function StudentRecordForm(props){
    const {
        values,
        setValues,
        handleInputChange,
        restForm
    } = useForm(initializeFValues);

    const rows = [
        createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
        createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
        createData('Eclair', 262, 16.0, 24, 6.0),
        createData('Cupcake', 305, 3.7, 67, 4.3),
        createData('Gingerbread', 356, 16.0, 49, 3.9),
      ];
    
    const handleSubmit = e => {
        e.preventDefault()

        insertStudentRecords(values);
        restForm();

        console.log("Rows before: " + rows);
        rows.push(createData(values))
        console.log("Rows after: " + rows);
    }

    return(
        <Form>
            <Input
                name= "fullName"
                label= "Full Name:"
                value= {values.fullName}
                onChange= {handleInputChange} />

            <Input
                name= "rollNo"
                label= "Rollno:"
                value= {values.rollNo}
                onChange= {handleInputChange} />
                
            <Input
                name= "studentClass"
                label= "Student Class:"
                value= {values.studentClass}
                onChange= {handleInputChange} />

            <Input
                name= "totalSubjects"
                label= "Total Subjects:"
                value= {values.totalSubjects}
                onChange= {handleInputChange} />

            <Input
                name= "age"
                label= "Age:"
                value= {values.age}
                onChange= {handleInputChange} />
            
            <div>
                <Button 
                    text = "Submit" 
                    type="submit" 
                    onClick= {handleSubmit} />
            </div>
        </Form>
    )
}

export default StudentRecordForm;
