import '@material/react-button/index.scss';
import { Button } from '@material/react-button';
import '@material/react-text-field/index.scss';
import TextField from '@material/react-text-field';
import { useState } from 'react';
import { Grid } from '@material/react-layout-grid';
import './StudentRecordForm.scss';
import useForm from '../useForm';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function StudentRecordForm(props){
    const {
        values,
        setValues
    } = useForm(initializeFValues);
    
    

        return(
            <form>
                <label>
                    Full Name:
                    <input type="text" 
                            name="fullName" 
                            value={values.fullName} 
                            onChange={handleInputChange} />
                </label>
                <label>
                    Rollno:
                    <input type="text" 
                            name="rollNo" 
                            value={values.rollNo}
                            onChange={handleInputChange} />
                </label>
                <label>
                    Student Class:
                    <input type="text" 
                            name="studentClass" 
                            value={values.studentClass}
                            onChange={handleInputChange} />
                </label>
                <label>
                    Total Subjects:
                    <input type="text" 
                            name="totalSubject" 
                            value={values.totalSubjects}
                            onChange={handleInputChange} />
                </label>
                <label>
                    Age:
                    <input type="text" 
                            name="age" 
                            value={values.age}
                            onChange={handleInputChange} />
                </label>
                <Button>Submit</Button>
            </form>
        )
}

export default StudentRecordForm;
