import './StudentRecordForm.scss';
import {useForm, Form} from '../useForm';
import Input from '../controls/Input';
import Button from '../controls/Button';
import {insertStudentRecords, getAllStudentRecords} from '../../services/studentRecordService';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function StudentRecordForm(props){
    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initializeFValues);
    
    const handleSubmit = e => {
        e.preventDefault()

        insertStudentRecords(values);

        const records = getAllStudentRecords();
        const list = records.studentRecords.map(item=> {<li>{item.fullName}</li>});
        window.alert(<ul>{list}</ul>);
    }

    return(
        <Form>
            <Input
                name= "fullName"
                label= "Full Name:"
                value= {values.fullName}
                onChange= {handleInputChange} />

            <Input
                name= "rollNo"
                label= "Rollno:"
                value= {values.rollNo}
                onChange= {handleInputChange} />
                
            <Input
                name= "studentClass"
                label= "Student Class:"
                value= {values.studentClass}
                onChange= {handleInputChange} />

            <Input
                name= "totalSubjects"
                label= "Total Subjects:"
                value= {values.totalSubjects}
                onChange= {handleInputChange} />

            <Input
                name= "age"
                label= "Age:"
                value= {values.age}
                onChange= {handleInputChange} />
            
            <div>
                <Button 
                    text = "Submit" 
                    type="submit" 
                    onClick= {handleSubmit} />
            </div>
        </Form>
    )
}

export default StudentRecordForm;
