import React, {useState} from "react";

export default function useForm(initializeFValues){
    const [values, setValues] = useState(initializeFValues);

    const handleInputChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]:value
        });
    }

    return{
        values,
        setValues,
        handleInputChange
    }
}
