import './StudentRecordForm.scss';
import {useForm, Form} from '../useForm';
import Input from '../controls/Input';
import Button from '../../components/controls/Button';
import { useEffect } from 'react';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function StudentRecordForm(props){
    const {
        values,
        setValues,
        handleInputChange,
        restForm
    } = useForm(initializeFValues);

    const handleSubmit = e => {
        e.preventDefault()
        props.saveStudentRecord(values);
        restForm();
    }

    useEffect((items)=>{
        if(props.recordForEdit != null)
            setValues({...props.recordForEdit
            })
    }, [props.edit])

    return(
        <Form>
            <Input
                name= "fullName"
                label= "Full Name:"
                value= {values.fullName}
                onChange= {handleInputChange} />

            <Input
                name= "rollNo"
                label= "Rollno:"
                value= {values.rollNo}
                onChange= {handleInputChange} />
                
            <Input
                name= "studentClass"
                label= "Student Class:"
                value= {values.studentClass}
                onChange= {handleInputChange} />

            <Input
                name= "totalSubjects"
                label= "Total Subjects:"
                value= {values.totalSubjects}
                onChange= {handleInputChange} />

            <Input
                name= "age"
                label= "Age:"
                value= {values.age}
                onChange= {handleInputChange} />

            <div>
                <Button 
                    text = "Submit" 
                    type="submit" 
                    onClick= {handleSubmit} />
            </div>
        </Form>
    )
}

export default StudentRecordForm;
