import './StudentRecordPage.scss';
import '@material/react-layout-grid/index.scss';
import { useState } from 'react';
import { Cell, Grid, Row } from '@material/react-layout-grid';
import {Paper, makeStyles, Divider} from '@material-ui/core';
import StudentRecordForm from '../SRForm/StudentRecordForm';
import StudentRecordList from '../SRList/StudentRecordList';

const useStyles = makeStyles(theme => ({
    PageContent: {
        width:'80%',
        margin: '0 auto',
        padding: theme.spacing(3)
    }
}))

const studentRecords = [
    {fullName: "Rebeca", rollNo: 1, studentClass: 2, totalSubjects: 5, age: 6 },
    {fullName: "Shawn", rollNo: 2, studentClass: 4, totalSubjects: 7, age: 8 },
    {fullName: "Patrik", rollNo: 3, studentClass: 2, totalSubjects: 5, age: 5 },
    {fullName: "Pokoyo", rollNo: 4, studentClass: 3, totalSubjects: 6, age: 7 },
    {fullName: "Elly", rollNo: 5, studentClass: 3, totalSubjects: 6, age: 6 },
    {fullName: "Spongebob", rollNo: 6, studentClass: 2, totalSubjects: 5, age: 5 }
]

export default function StudentRecordPage(props){
    const [records, setRecords] = useState(studentRecords)
    const [updateRecord, setUpdateRecord] = useState(false)
    const classes = useStyles();

    const saveStudentRecordHandler = (studentRecord) => {
        studentRecords.push(studentRecord);
        setUpdateRecord(true);
    }

    return(
        <Paper className={classes.PageContent}>
            <p className="title">Simple Student Records</p>
            <Divider/>
            <Grid>
                <Row>
                    <Cell columns={5}>
                        <StudentRecordForm saveStudentRecord={saveStudentRecordHandler}/>
                    </Cell>
                    <Cell columns={7}>
                        <StudentRecordList studentRecords={records}/>
                        {setUpdateRecord(false)}
                    </Cell>
                </Row>
            </Grid>
            
        </Paper>
    )
}
