import './StudentRecordList.scss';
import { Table, TableHead, TableBody, TableCell, TableRow } from '@material-ui/core';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import CloseIcon from '@material-ui/icons/Close';
import {getAllStudentRecords} from '../../services/studentRecordService';
import { useState } from 'react';
import ActionButton from '../../components/controls/ActionButton';
import Popup from '../Popup';
import StudentRecordForm from '../SRForm/StudentRecordForm';

function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

function StudentRecordList(props){
    const [records, setRecords] = useState(getAllStudentRecords())
    const [openPopup, setOpenPopup] = useState(false);
    const [confirmDialog, setConfirmDialog] = useState({ isOpen:false, title:'', subTitle:''});

    const onDelete = id => {
        setConfirmDialog({
            ...confirmDialog, isOpen:false
        })
        
    }
    
    const rows = [
        createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
        createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
        createData('Eclair', 262, 16.0, 24, 6.0),
        createData('Cupcake', 305, 3.7, 67, 4.3),
        createData('Gingerbread', 356, 16.0, 49, 3.9),
      ];

      
    //const studentRecordList = list.map((item) => <TableCell> { item.fullName } </TableCell>);

    return(
        <div>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>FullName</TableCell>
                        <TableCell align="right">Roll No.</TableCell>
                        <TableCell align="right">Student Class</TableCell>
                        <TableCell align="right">Total Subjects</TableCell>
                        <TableCell align="right">Age</TableCell>
                        <TableCell> </TableCell>
                        <TableCell> </TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <TableRow key={row.name} sx={{ '&:last-child td, &:last-child th': { border: 0 } }} >
                            <TableCell component="th" scope="row">
                                {row.name}
                            </TableCell>
                            <TableCell align="right">{row.calories}</TableCell>
                            <TableCell align="right">{row.fat}</TableCell>
                            <TableCell align="right">{row.carbs}</TableCell>
                            <TableCell align="right">{row.protein}</TableCell>
                            <TableCell>
                                <ActionButton
                                    color = "primary"
                                    onClick = {() => setOpenPopup(true)}
                                    >
                                    <EditOutlinedIcon fontSize="small"/>
                                </ActionButton>
                            </TableCell>
                            <TableCell>
                                <ActionButton
                                    color = "secondary"
                                    onClick={()=> {setConfirmDialog({
                                        isOpen:true,
                                        title:"Delete Student Record",
                                        subTitle:"Do you want to delete Student Record?",
                                        onConfirm:()=>{onDelete(row.name)}
                                    })}}>
                                    <CloseIcon fontSize="small"/>
                                </ActionButton>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
            <Popup title="Edit Student Record" 
                    openPopup = {openPopup} 
                    setOpenPopup = {setOpenPopup} >
                <StudentRecordForm/>
            </Popup>
        </div>
    )
}

export default StudentRecordList;
