import './StudentRecordForm.scss';
import {useForm, Form} from '../useForm';
import Input from '../controls/Input';
import Button from '../../components/controls/Button';
import { useEffect} from 'react';

const initializeFValues = {
    fullName: '',
    rollNo: '',
    studentClass: '',
    totalSubjects: '',
    age: ''
}

function StudentRecordForm(props){
    const {studentRecordForEdit} = props

    const validate = () => {
        let temp = {}
        temp.fullName= values.fullName?"": "This field is required."
        temp.rollNo= typeOf(values.rollNo === Number)?"": "Enter only numbers."
        temp.studentClass= typeOf(values.studentClass === Number)?"": "This field is required."
        temp.totalSubjects= typeOf(values.totalSubjects === Number)?"": "This field is required."
        temp.age= typeOf(values.age === Number)?"": "This field is required."
    }

    const {
        values,
        setValues,
        handleInputChange,
        restForm
    } = useForm(initializeFValues);

    const handleSubmit = e => {
        e.preventDefault()
        props.saveStudentRecord(values);
        restForm();
    }

    useEffect(()=>{
        if(studentRecordForEdit != null){
                setValues({...studentRecordForEdit
            })
        }
           
    }, [studentRecordForEdit])

    const addButtons = (props.source === "newRecord") ?
         <Button text = "Submit"  type="submit" onClick= {handleSubmit} /> :
         <div>
             <Button text = "Save"  type="submit" onClick= {handleSubmit} /> 
         </div>

    return(
        <Form>
            <Input
                name= "fullName"
                label= "Full Name:"
                value= {values.fullName}
                onChange= {handleInputChange} />

            <Input
                name= "rollNo"
                label= "Rollno:"
                value= {values.rollNo}
                onChange= {handleInputChange} />
                
            <Input
                name= "studentClass"
                label= "Student Class:"
                value= {values.studentClass}
                onChange= {handleInputChange} />

            <Input
                name= "totalSubjects"
                label= "Total Subjects:"
                value= {values.totalSubjects}
                onChange= {handleInputChange} />

            <Input
                name= "age"
                label= "Age:"
                value= {values.age}
                onChange= {handleInputChange} />

            <div>
                {addButtons}
            </div>
        </Form>
    )
}

export default StudentRecordForm;
